INSERT INTO public.product_category VALUES (141, 'Бену продавница нуди сокове од воћа и поврћа из брендова Биотта и Цоццо Јуице. Сокови од цвекле, црне рибизле, нара, бруснице и кокос поред освежавања, имају и антиоксидативно и антимикробно деловање и позитивно утичу на јачање имуног система.
